ValidationRule.errorConditionFormula:
AND(
NOT(isNEW()), NOT(ValidationBypass__c == True),
OR( AND( ISPICKVAL(Status, "Contacted"),NOT(ISCHANGED(Status)) ), ISPICKVAL(Status, "Nurturing")
),
NOT(ValidationBypass__c == True), OR( AND(NOT(ISBLANK(text(Sector__c))),ISCHANGED(Sector__c) ), AND(NOT(ISBLANK(text(Customer_Sub_segment_Sub_Vertical__c))),ISCHANGED(Customer_Sub_segment_Sub_Vertical__c) ), ISCHANGED(Customer_Segment_Vertical__c) , ISCHANGED(Requirement__c), AND(NOT(ISBLANK(Email)),ISCHANGED(Email) ), AND(NOT(ISBLANK(Text(Customer_Category__c))),ISCHANGED(Customer_Category__c) ), AND(NOT(ISBLANK(Text(Type_of_Customer__c))),ISCHANGED(Type_of_Customer__c) )
) )
