ValidationRule.errorConditionFormula:
AND(
  CONTAINS(
    UPPER(
      VLOOKUP(
        $ObjectType.cgc_sync__Sync_Mobile_App_Theme__c.Fields.Name,
        $ObjectType.cgc_sync__Sync_Mobile_App_Theme__c.Fields.Name, Name
      )
    ),
    UPPER(Name)
  ),
  OR(
    ISNEW(), UPPER(PRIORVALUE(Name)) != UPPER(Name)
    )
  )
