ValidationRule.errorConditionFormula:
AND(RecordType.DeveloperName = 'Promotion',
          OR(
            AND(NOT(ISBLANK(cgcloud__Order_Date_From__c)), cgcloud__Date_From__c > cgcloud__Order_Date_From__c),
            AND(NOT(ISBLANK(cgcloud__Order_Date_Thru__c)), cgcloud__Order_Date_Thru__c > cgcloud__Date_Thru__c)
          )
        )
