ValidationRule.errorConditionFormula:
AND(
          OR(
          ISPICKVAL( cgcloud__Status__c ,'Running'),
          ISPICKVAL( cgcloud__Status__c ,'Completed')
          ),
          AND(
          ISPICKVAL(cgcloud__Consider_Vehicle__c,'TruckTrailer'),
          OR(
          ISBLANK( cgcloud__Truck__c ),
          ISBLANK(  cgcloud__Trailer_1__c )
          )
          )
          )
